import React, { useState } from "react";
import Video from "./Video";
const Form =(props)=>{
 const [video,setVideo] = useState({});
 const [addData, setAddData] = useState({
   url: "",
   title: "",
 });

//  const titleChangeHandler =(event)=>{
//     setEnteredTitle(event.target.value);
//  }
 const ChangeHandler = (event) => {
   const inputName = event.target.getAttribute("name");
   const inputValue = event.target.value;
   const addedVideo = { ...addData };
    addedVideo[inputName] = inputValue;
   setAddData(addedVideo);
 };
 const submitHandler = event=>{
  event.preventDefault();
  console.log(addData);
  const newVideo = {
    url: addData.url,
    title: addData.title,
  };
  let videos = [...video, newVideo];
  setVideo(videos)

 }

 return (
   <div>
     <form onSubmit={submitHandler}>
       <label htmlFor="title">Title</label>
       <input
         type="text"
         id="title"
         onChange={ChangeHandler}
         value={addData.title}
       ></input>
       <label htmlFor="video-link">URL</label>
       <input
         type="url"
         id="video-link"
         onChange={ChangeHandler}
         value={addData.url}
       ></input>
       <button>Add</button>
     </form>
     {video.map((element, index) => {
       return (
         <Row
           key={index}
           Id={element.id}
           Title={element.title}
           FirstName={element.firstName}
           SureName={element.surname}
           Email={element.email}
           CheckOutDate={element.checkOutDate}
           RoomId={element.roomId}
           CheckInDate={element.checkInDate}
         />
       );
     })}
   </div>
 );
}
export default Form;
