import React, { useState } from "react";
import Video from "./Video";
const Form =(props)=>{
  let firstVideo = {url:"https://www.youtube.com/embed/qw--VYLpxG4",title:"halla"};
 const [video,setVideo] = useState([firstVideo]);
 const [addData, setAddData] = useState({
   url: "",
   title: ""
 });

//  const titleChangeHandler =(event)=>{
//     setEnteredTitle(event.target.value);
//  }
 const ChangeHandler = (event) => {
   const inputName = event.target.getAttribute("name");
   const inputValue = event.target.value;
   const addedVideo = { ...addData };
    addedVideo[inputName] = inputValue;
   setAddData(addedVideo);
 };
 const submitHandler = event=>{
  event.preventDefault();
  console.log(addData);
  const newVideo = {
    url: addData.url,
    title: addData.title,
  };
  let videos = [...video, newVideo];
  setVideo(videos);
  setAddData({
    url: "",
    title: ""
  });

 }

 return (
   <div>
     <form onSubmit={submitHandler} className="">
       <label htmlFor="title">Title</label>
       <input
         type="text"
         id="title"
         name="title"
         onChange={ChangeHandler}
         value={addData.title}
       ></input>
       <label htmlFor="video-link">URL</label>
       <input
         type="url"
         id="video-link"
         name="url"
         onChange={ChangeHandler}
         value={addData.url}
       ></input>
       <button>Add</button>
     </form>
     {video.map((element, index) => {
       return (
         <Video
           key={index}
           URL={element.url}
           Title={element.title}
         />
       );
     })}
   </div>
 );
}
export default Form;
