import React, {useState } from "react";
import { BsFillHandThumbsUpFill } from "react-icons/bs";
import { BsFillHandThumbsDownFill } from "react-icons/bs";
import "./App.css";
import example from "./exampleresponse.json";

const ListOfVideos = (props) => {
 const [like,setLike] =useState(0);
 const [disLike,setDisLike] =useState(0);
 const [oneVideo,setOneVideo] = useState(example);



 const clickLike =()=>{

  setLike(like=>like+1);

 }
 const clickDisLike =()=>{
  setDisLike(disLike=>disLike+1);

 }

 const deleteHandler = (Id)=>{
   setOneVideo(oneVideo.filter((item)=>item.id!==Id))


 }
  return (
    <div className="visible">

      {oneVideo.map((item,index)=>{
       return (
         <div key={index}>
           <div className="vote">
             <BsFillHandThumbsUpFill onClick={clickLike} className="thumb" />
             <span>{like}</span>
             <h2>Vote</h2>

             <BsFillHandThumbsDownFill
               onClick={clickDisLike}
               className="thumb"
             />
             <span>{disLike}</span>
           </div>

           <h3>{item.title}</h3>
           <iframe
             width="300"
             height="300"
             src={item.url.replace("watch?v=", "embed/")}
             title={item.title}
             frameBorder="0"
             allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
             allowFullScreen
           ></iframe>
           <button
             onClick={() => deleteHandler(item.id)}
             type="button"
             className="btn btn-primary mt-3"
           >
             Delete
           </button>
         </div>
       );
      })};
      </div>
  );
};
export default ListOfVideos;
