import React, { useState, useEffect } from "react";
import "./App.css";

// import results from "./exampleresponse.json";
import Form from "./Form";
import ListOfVideos from "./ListOfVideos";
import "./App.css";
import OrderButton from "./OrderButton";
import example from "./exampleresponse.json";

function App(props) {
  const [order, setOrder] = useState([]);
  const [clicked,setClicked] = useState(false);
  const orderHandler = () => {
    setClicked(true)
    if(clicked){
    let asc = example.sort((a, b) => a.rating - b.rating);
    setOrder(asc);
    setClicked(false)
    }
    if(!clicked){
      let desc = example.sort((a, b) => b.rating - b.rating);
      setOrder(desc);
      setClicked(true);
    }
  };
  // const sorted_asc = videos.sort((a, b) => a.rating - b.rating);

  return (
    <div className="App">
      <header className="App-header bg-primary text-white">
        <h1>Video Recommendation</h1>
      </header>
      <OrderButton />
      <Form />

      <ListOfVideos example={example} />
    </div>
  );
}

export default App;
