import React, { useState, useEffect } from "react";
import { BsFillHandThumbsUpFill } from "react-icons/bs";
import { BsFillHandThumbsDownFill } from "react-icons/bs";
import "./App.css";


const ListOfVideos = (props) => {
  const [like, setLike] = useState(0);
  const [disLike, setDisLike] = useState(0);
  const [oneVideo, setOneVideo] = useState([]);
  const [order, setOrder] = useState([]);
  const [descOrder, setDescOrder] = useState([]);//replace with oneVideo when try to fetch
  const [clicked,setClicked] = useState(true);
  const clickLike = () => {
    setLike((like) => like + 1);
  };
  const clickDisLike = () => {
    setDisLike((disLike) => disLike + 1);
  };

  const deleteHandler = (Id) => {
    setOneVideo(oneVideo.filter((item) => item.id !== Id));
  };

  const videosData = () => {
    fetch("https://myvideos-api.herokuapp.com/") //try https://video-rec.herokuapp.com
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
      })
      .then((data) => {
        console.log(data);
        setOneVideo(data);
      });
  };
  useEffect(() => {
    videosData();
  }, []);

  const Data = () => {
    fetch("https://myvideos-api.herokuapp.com/?order=asc") //try https://video-rec.herokuapp.com
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
      })
      .then((data) => {
        console.log(data);
        setOrder(data);
      });
  };
  useEffect(() => {
  Data();
  }, []);

  const DescData = () => {
    fetch("https://myvideos-api.herokuapp.com/") //try https://video-rec.herokuapp.com
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
      })
      .then((data) => {
        console.log(data);
        setDescOrder(data);
      });
  };
  useEffect(() => {
    DescData();
  }, []);

  const orderHandler = () => {

    setClicked(true);
    if (clicked) {
      setOneVideo(order);

      setClicked(false);
    }
    if (!clicked) {
      setOneVideo(descOrder);

      setClicked(true);
    }
  };

  return (
    <div>
      <h6>Sort By Rating</h6>
      <button onClick={orderHandler} className="btn btn-primary mt-1">
        desc
      </button>

      <div className="video-container">
        {oneVideo.map((item, index) => {
          return (
            <div key={index} className="visible">
              <div className="vote">
                <BsFillHandThumbsUpFill onClick={clickLike} className="thumb" />
                <span>{like}</span>
                <h5>Vote</h5>
                <BsFillHandThumbsDownFill
                  onClick={clickDisLike}
                  className="thumb"
                />
                <span>{disLike}</span>
              </div>
              <h4>Rating:{item.rating}</h4>

              <p>{item.title}</p>
              <iframe
                width="300"
                height="300"
                src={item.url.replace("watch?v=", "embed/")}
                // src={item.vurl.replace("watch?v=", "embed/")} when try:https://video-rec.herokuapp.com
                title={item.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              ></iframe>
              <button
                onClick={() => deleteHandler(item.id)}
                type="button"
                className="btn btn-primary mt-3"
              >
                Delete
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
};
export default ListOfVideos;
