import React, {useState } from "react";
import { FaThumbsUp } from "react-icons/fa";
import { FaThumbsDown } from "react-icons/fa";
import "./App.css";

const Video = (props) => {
 const [like,setLike] =useState(0);
 const [disLike,setDisLike] =useState(0);
 const clickLike =()=>{

  setLike(like=>like+1);

 }
 const clickDisLike =()=>{
  setDisLike(disLike=>disLike+1);

 }
 const [clicked,setClicked] = useState(false);
 const deleteHandler = ()=>{
   setClicked(true);


 }



  return (
    <div className={clicked ? "visible" : "vi"}>
      <div>
        <h3> {props.title}</h3>
        <div className="vote">
          <FaThumbsUp onClick={clickLike} className="thumb" />

          <span>{like}</span>
          <h2>Vote</h2>
          <FaThumbsDown onClick={clickDisLike} className="thumb" />
          <span>{disLike}</span>
        </div>
      </div>

      <iframe
        width="560"
        height="315"
        src={props.src}
        title={props.title}
        frameBorder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowFullScreen
      ></iframe>
      <button   onClick={deleteHandler}>
        Delete
      </button>
    </div>
  );
};
export default Video;
