import React, { useState, useEffect } from "react";

import "./App.css";
import OrderButton from "./OrderButton";
import ThumbUp from "./ThumbUp";
import ThumbDown from "./ThumbUp";

const ListOfVideos = (props) => {
  const [disLike, setDisLike] = useState(0);
  const [oneVideo, setOneVideo] = useState([]);
  const [order, setOrder] = useState([]);
  const [descOrder, setDescOrder] = useState([]);
  const [clicked, setClicked] = useState(true);

  const clickDisLike = () => {
    setDisLike((disLike) => disLike + 1);
  };

  const deleteHandler = (Id) => {
    setOneVideo(oneVideo.filter((item) => item.id !== Id));
  };

  const videosData = () => {
    fetch("https://myvideos-api.herokuapp.com/") //I deployed my api app to heroku as I was not able to connect to my local host
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
      })
      .then((data) => {
        console.log(data);
        setOneVideo(data);
        setDescOrder(data);
      });
  };
  useEffect(() => {
    videosData();
  }, []);

  const Data = () => {
    fetch("https://myvideos-api.herokuapp.com/?order=asc") //try https://video-rec.herokuapp.com
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
      })
      .then((data) => {
        console.log(data);
        setOrder(data);
      });
  };
  useEffect(() => {
    Data();
  }, []);
  const orderHandler = () => {
    setClicked(true);
    if (clicked) {
      setOneVideo(order);
      setClicked(false);
    }
    if (!clicked) {
      setOneVideo(descOrder);
      setClicked(true);
    }
  };

  return (
    <div>
      <OrderButton order={orderHandler} />
      <div className="video-container">
        {oneVideo.map((item, index) => {
          return (
            <div key={index} className="visible">
              <div className="vote">
                <ThumbUp />

                <h5>Vote</h5>
                <ThumbDown/>

                <span>{disLike}</span>
              </div>
              <h4>Rating:{item.rating}</h4>

              <p>{item.title}</p>
              <iframe
                width="300"
                height="300"
                src={item.url.replace("watch?v=", "embed/")}
                title={item.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              ></iframe>
              <button
                onClick={() => deleteHandler(item.id)}
                type="button"
                className="btn btn-primary mt-3"
              >
                Delete
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
};
export default ListOfVideos;
