import React, { useState } from "react";
import Video from "./Video";
const Form =(props)=>{
 const [enteredTitle,setEnteredTitle] = useState("");
 const [enteredUrl, setEnteredUrl] = useState("");
 const [inputArray,setInputArray]= useState([])

 const titleChangeHandler =(event)=>{
    setEnteredTitle(event.target.value);
 }
 const urlChangeHandler = (event) => {
   setEnteredUrl(event.target.value);
 };
 const submitHandler = event=>{
  event.preventDefault();
  console.log(enteredTitle);
  console.log(enteredUrl);
  let obje = { title: enteredTitle, url: enteredUrl };
  setInputArray([obje]);
  obje = {
    title: "",
    url: "",
  };

 }


 return (
   <div>
     <form onSubmit={submitHandler} class = "float-left">
       <div class="form-group ml-5 ">
         <label htmlFor="title">Title</label>
         <input
           type="text"
           id="title"
           onChange={titleChangeHandler}
           class="col-xs-3"
         ></input>
       </div>
       <div class="form-group ml-">
         <label htmlFor="video-link">URL</label>
         <input
           type="url"
           id="video-link"
           onChange={urlChangeHandler}
           class="col-xs-3 "
         ></input>
       </div>
       <button type="button" class="btn btn-primary ml-2">
         Add
       </button>
     </form>
     <Video
       src="https://www.youtube.com/embed/qw--VYLpxG4"
       title="Learn PostgreSQL Tutorial "
     />
     {inputArray.map((item, index) => (
       <Video src={item.url} title={item.title} key={index} />
     ))}
   </div>
 );
}
export default Form;
